mapper.select:
select count(pin.reserve) as laveNum, di.id, di.code, di.name, di.content, di.raw_material, di.portion, di.taste, di.unit_price, di.receive_unit_price, di.integral_unit, di.prescription_flag, di.sale_num, di.status, di.merchant_id, di.create_date, di.images, di.heat, di.protein, di.fat, di.type_id, pi2.name as pharmacyName, pi2.code as pharmacyCode, di.name as drugName, di.portion, di.raw_material, di.images, ti.name as typeName from pharmacy_inventory pin left join merchant_info pi2 on (pi2.id = pin.pharmacy_id) left join dishes_info di on (di.id = pin.drug_id) left join type_info ti on (ti.id = di.type_id) where 1 = 1 AND pin.shelf_status = 1 AND pin.pharmacy_id = #{pharmacyId} AND di.status = 1 AND pin.drug_id IN
#{item}
GROUP BY pin.pharmacy_id, pin.drug_id HAVING count(pin.reserve) > 0
